mapper.select:
SELECT COUNT(*)
FROM server_info
WHERE server_ip = #{ip}

select server_id
from server_info
where server_ip=#{serverIp}

mapper.update:
UPDATE server_info
SET server_os=#{serverOs}, server_hostname=#{serverHostname}, memory_total=#{memoryTotal}, purpose=#{purpose},
server_ip=#{serverIp}
WHERE server_id=#{serverId};
